// 1. Get the AI's analysis
const text = $input.item.json.content.parts[0].text;
const cleanText = text.replace(/```json|```/g, '').trim();
const aiData = JSON.parse(cleanText);

// 2. Get the original job DIRECTLY from the Loop node
// (We use .first() because the loop gives us 1 item at a time)
const originalJob = $('Loop Over Items').first().json;

// 3. Merge them together
return {
  ...originalJob,
  ...aiData
};
